import { useState, useEffect } from "react";

function App() {
  const [todo, setTodo] = useState("");
  const writeTodo = (event) => {
    setTodo(event.value);
  };
  return (
    <div>
      <input
        onChange={writeTodo}
        value={todo}
        type="text"
        placeholder="Write your todo"
      ></input>
      <button>submit!</button>
      <ul>{}</ul>
    </div>
  );
}

export default App;
